drillDownQuery.query:
SELECT * FROM demographic d
-- ROOT CONTROLS THE IDENTIFICATION OF ALL DIABETIC PATIENTS. INNER JOIN dxresearch dxr ON ( d.demographic_no = dxr.demographic_no)
-- COUNT ALL A1C MEASUREMENTS. COUNTS ALL TESTS PER PATIENT. LEFT JOIN ( SELECT COUNT(*) AS a1cnumber, demographicNo FROM measurements WHERE type LIKE "A1C" AND DATE(dateObserved) > DATE_SUB( ${ upperLimit.to }, INTERVAL ${ lowerLimit.from } MONTH ) AND demographicNo > 0 GROUP BY demographicNo HAVING COUNT(demographicNo) > -1 ) A1C ON (d.demographic_no = A1C.demographicNo) WHERE d.patient_status LIKE "${ active }" AND dxr.coding_system LIKE "icd9" AND dxr.dxresearch_code IN ${ dxcodelist } AND dxr.status NOT LIKE "%D%" AND d.demographic_no > 0 AND d.provider_no LIKE '${ provider }' AND ( ${lowerLimit.age} <= ROUND( ABS( DATEDIFF( DATE( CONCAT(d.year_of_birth,"-",d.month_of_birth,"-",d.date_of_birth) ), DATE_SUB( ${ upperLimit.to }, INTERVAL ${ lowerLimit.from } MONTH ) ) ) / 365.25 ) ) AND d.demographic_no NOT IN ${excludedPatient}
